// chapters/quizzesContent.js
const quizzesContent = {
    ru: [
        {
            question: "Какой язык программирования используется для веб-разработки?",
            options: ["Java", "Python", "JavaScript", "C#"],
            answer: "JavaScript"
        },
        {
            question: "Что такое React?",
            options: ["Библиотека для создания интерфейсов", "Язык программирования", "Система управления базами данных"],
            answer: "Библиотека для создания интерфейсов"
        },
        // Добавьте больше вопросов по мере необходимости
    ],
    en: [
        {
            question: "Which programming language is used for web development?",
            options: ["Java", "Python", "JavaScript", "C#"],
            answer: "JavaScript"
        },
        {
            question: "What is React?",
            options: ["Library for building interfaces", "Programming language", "Database management system"],
            answer: "Library for building interfaces"
        },
        // Добавьте больше вопросов по мере необходимости
    ],
};

export default quizzesContent;
