import React, { useState } from 'react';

const Lecture = ({ content, language }) => {
    const [currentPart, setCurrentPart] = useState(0);
    const [showQuestion, setShowQuestion] = useState(false);
    const [answer, setAnswer] = useState('');

    const lectureContent = content[language];

    const handleAnswer = (isCorrect) => {
        if (isCorrect) {
            setCurrentPart(currentPart + 1);
            setShowQuestion(false);
        } else {
            alert('Попробуйте снова!');
        }
    };

    return (
        <div>
            <h2>Часть {currentPart + 1}</h2>
            <p>{lectureContent[currentPart].text}</p>
            <button onClick={() => setShowQuestion(true)}>Следующая часть</button>
            {showQuestion && (
                <div>
                    <p>{lectureContent[currentPart].question}</p>
                    <input value={answer} onChange={(e) => setAnswer(e.target.value)} />
                    <button onClick={() => handleAnswer(answer === lectureContent[currentPart].correctAnswer)}>
                        Ответить
                    </button>
                </div>
            )}
        </div>
    );
};

export default Lecture;
