import React from 'react';

const Quiz = ({ content }) => {
    return (
        <div>
            <h2>Тесты</h2>
            {content.map((item, index) => (
                <div key={index}>
                    <p>{item.question}</p>
                    <ul>
                        {item.options.map((option, idx) => (
                            <li key={idx}>{option}</li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    );
};

export default Quiz;
