// chapters/practiceContent.js
const practiceContent = {
    ru: [
        {
            task: "Напишите функцию, которая возвращает квадрат числа.",
            example: "Пример: square(4) => 16"
        },
        {
            task: "Создайте массив и выведите его длину.",
            example: "Пример: let arr = [1, 2, 3]; // Длина: 3"
        },
        // Добавьте больше заданий по мере необходимости
    ],
    en: [
        {
            task: "Write a function that returns the square of a number.",
            example: "Example: square(4) => 16"
        },
        {
            task: "Create an array and print its length.",
            example: "Example: let arr = [1, 2, 3]; // Length: 3"
        },
        // Добавьте больше заданий по мере необходимости
    ],
};

export default practiceContent;
