import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Lecture from './components/Lecture';
import Quiz from './components/Quiz';
import Practice from './components/Practice';
import Dictionary from './components/Dictionary'; // Импортируйте компонент словаря
import lecturesContent from './chapters/LecturesContent';
import quizzesContent from './chapters/quizzesContent'; // Импорт контента тестов
import practiceContent from './chapters/practiceContent'; // Импорт контента практики

const App = () => {
    const [language, setLanguage] = useState('ru');

    const handleLanguageChange = (newLanguage) => {
        setLanguage(newLanguage);
    };

    return (
        <Router>
            <Header onLanguageChange={handleLanguageChange} />
            <Routes>
                <Route path="/" element={<h2>Добро пожаловать в электронный учебник!</h2>} />
                <Route path="/lectures" element={<Lecture content={lecturesContent} language={language} />} />
                <Route path="/quizzes" element={<Quiz content={quizzesContent} />} /> {/* Передаем контент тестов */}
                <Route path="/practices" element={<Practice content={practiceContent} />} /> {/* Передаем контент практики */}
                <Route path="/dictionary" element={<Dictionary language={language} />} />
            </Routes>
        </Router>
    );
};

export default App;
